  // create an alien using the frame name..\n    const alien = Sprite.from(frameName);\n\n    alien.tint = Math.random() * 0xffffff;\n\n    alien.x = Math.random() * app.screen.width - app.screen.width / 2;\n    alien.y = Math.random() * app.screen.height - app.screen.height / 2;\n    alien.anchor.x = 0.5;\n    alien.anchor.y = 0.5;\n    aliens.push(alien);\n    alienContainer.addChild(alien);\n  }\n\n  // Combines both mouse click + touch tap\n  app.stage.on('pointertap', onClick);\n\n  function onClick() {\n    alienContainer.cacheAsTexture(!alienContainer.isCachedAsTexture);\n  }\n\n  app.ticker.add(() => {\n    // let's rotate the aliens a little bit\n    for (let i = 0; i < 100; i++) {\n      const alien = aliens[i];\n\n      alien.rotation += 0.1;\n    }\n\n    count += 0.01;\n\n    alienContainer.scale.x = Math.sin(count);\n    alienContainer.scale.y = Math.sin(count);\n    alienContainer.rotation += 0.01;\n  });\n})();\n"}}),"\n"]}),"\n",(0,a.jsxs)(n.li,{children:["\n",(0,a.jsxs)(n.p,{children:[(0,a.jsx)(n.strong,{children:"pixelLine"}),": The ",(0,a.jsx)(n.code,{children:"pixelLine"})," property is a neat feature of the PixiJS Graphics API that allows you to create lines that remain 1 pixel thick, regardless of scaling or zoom level. This feature is especially useful for achieving crisp, pixel-perfect visuals, particularly in retro-style or grid-based games, technical drawing, or UI rendering."]}),"\n",(0,a.jsx)(n.admonition,{title:"Guide",type:"info",children:(0,a.jsxs)(n.p,{children:["We have a full guide on this feature ",(0,a.jsx)(n.a,{href:"/8.x/guides/components/scene-objects/graphics/graphics-pixel-line",children:"here"}),"."]})}),"\n",(0,a.jsx)(s._,{files:{"index.js":"// description: This example compares standard lines vs pixel-perfect lines using the Graphics class\nimport { Application, Container, Graphics, Text } from 'pixi.js';\n\n/**\n * Creates a grid pattern using Graphics lines\n * @param graphics - The Graphics object to draw on\n * @returns The Graphics object with the grid drawn\n */\nfunction buildGrid(graphics) {\n  // Draw 10 vertical lines spaced 10 pixels apart\n  for (let i = 0; i < 11; i++) {\n    // Move to top of each line (x = i*10, y = 0)\n    graphics\n      .moveTo(i * 10, 0)\n      // Draw down to bottom (x = i*10, y = 100)\n      .lineTo(i * 10, 100);\n  }\n\n  // Draw 10 horizontal lines spaced 10 pixels apart\n  for (let i = 0; i < 11; i++) {\n    // Move to start of each line (x = 0, y = i*10)\n    graphics\n      .moveTo(0, i * 10)\n      // Draw across to end (x = 100, y = i*10)\n      .lineTo(100, i * 10);\n  }\n\n  return graphics;\n}\n\n(async () => {\n  // Create and initialize a new PixiJS application\n  const app = new Application();\n\n  await app.init({ antialias: true, resizeTo: window });\n  document.body.appendChild(app.canvas);\n\n  // Create two grids - one with pixel-perfect lines and one without\n  const gridPixel = buildGrid(new Graphics()).stroke({ color: 0xffffff, pixelLine: true, width: 1 });\n\n  const grid = buildGrid(new Graphics()).stroke({ color: 0xffffff, pixelLine: false });\n\n  // Position the grids side by side\n  grid.x = -100;\n  grid.y = -50;\n  gridPixel.y = -50;\n\n  // Create a container to hold both grids\n  const container = new Container();\n\n  container.addChild(grid, gridPixel);\n\n  // Center the container on screen\n  container.x = app.screen.width / 2;\n  container.y = app.screen.height / 2;\n  app.stage.addChild(container);\n\n  // Animation variables\n  let count = 0;\n\n  // Add animation to scale the grids over time\n  app.ticker.add(() => {\n    count += 0.01;\n    container.scale = 1 + (Math.sin(count) + 1) * 2;\n  });\n\n  // Add descriptive label\n  const label = new Text({\n    text: 'Grid Comparison: Standard Lines (Left) vs Pixel-Perfect Lines (Right)',\n    style: { fill: 0xffffff },\n  });\n\n  // Position label in top-left corner\n  label.position.set(20, 20);\n  label.width = app.screen.width - 40;\n  label.scale.y = label.scale.x;\n  app.stage.addChild(label);\n})();\n"}}),"\n"]}),"\n",(0,a.jsxs)(n.li,{children:["\n",(0,a.jsxs)(n.p,{children:[(0,a.jsx)(n.strong,{children:"New Global Methods"}),": We have added three new functions to Container to make it easier to work with global transforms / tints / alphas."]}),"\n",(0,a.jsxs)(n.ul,{children:["\n",(0,a.jsxs)(n.li,{children:["\n",(0,a.jsx)(n.p,{children:(0,a.jsx)(n.strong,{children:"getGlobalTransform"})}),"\n",(0,a.jsx)(n.pre,{children:(0,a.jsx)(n.code,{className:"language-ts",children:"const skipUpdate = false;\nconst outMatrix = new Matrix();\n\n// writes and returns outMatrix;\nconst globalTransform = container.getGlobalTransform(outMatrix, skipUpdate);\n"})}),"\n"]}),"\n",(0,a.jsxs)(n.li,{children:["\n",(0,a.jsx)(n.p,{children:(0,a.jsx)(n.strong,{children:"getGlobalTint"})}),"\n",(0,a.jsx)(n.pre,{children:(0,a.jsx)(n.code,{className:"language-ts",children:"const skipUpdate = false;\n// returns rgb color\nconst globalTint = container.getGlobalTint(skipUpdate);\n"})}),"\n"]}),"\n",(0,a.jsxs)(n.li,{children:["\n",(0,a.jsx)(n.p,{children:(0,a.jsx)(n.strong,{children:"getGlobalAlpha"})}),"\n",(0,a.jsx)(n.pre,{children:(0,a.jsx)(n.code,{className:"language-ts",children:"const skipUpdate = false;\n// returns alpha as number;\nconst globalAlpha = container.getGlobalAlpha(skipUpdate);\n"})}),"\n"]}),"\n"]}),"\n",(0,a.jsxs)(n.admonition,{title:"INFO",type:"info",children:[(0,a.jsxs)(n.p,{children:["if ",(0,a.jsx)(n.code,{children:"skipUpdate"})," is true - it will be faster but may be outdated - uses the last rendered data"]}),(0,a.jsxs)(n.p,{children:["if ",(0,a.jsx)(n.code,{children:"skipUpdate"})," is false - it will be 100% accurate but slower - recalculates transform chain"]})]}),"\n"]}),"\n"]}),"\n",(0,a.jsx)(n.h3,{id:"v850",children:"v8.5.0"}),"\n",(0,a.jsxs)(n.ul,{children:["\n",(0,a.jsxs)(n.li,{children:["\n",(0,a.jsxs)(n.p,{children:[(0,a.jsx)(n.strong,{children:"ParticleContainer"}),": Faster than ever, optimized for rendering a million particles effortlessly."]}),"\n",(0,a.jsxs)(n.p,{children:["The ",(0,a.jsx)(n.code,{children:"ParticleContainer"})," shines when you need insane numbers of visual elements on-screen, especially when you want them moving and interacting in real time. Whether you're building particle effects, swarms of characters, or abstract art installations, PixiJS v8 has you covered. The static vs. dynamic property system gives you granular control over performance, allowing you to fine-tune the balance between flexibility and speed."]}),"\n",(0,a.jsx)(n.admonition,{title:"Blog",type:"info",children:(0,a.jsxs)(n.p,{children:["We have a blog post on this feature ",(0,a.jsx)(n.a,{href:"/blog/particlecontainer-v8",children:"here"}),"."]})}),"\n",(0,a.jsx)(s._,{files:{"index.js":"// description: This example demonstrates how to create a particle system using a Container to manage multiple sprites\nimport { Application, Assets, Container, Rectangle, Sprite } from 'pixi.js';\n\n(async () => {\n  // Create a new application\n  const app = new Application();\n\n  // Initialize the application\n  await app.init({ background: '#1099bb', resizeTo: window });\n\n  // Append the application canvas to the document body\n  document.body.appendChild(app.canvas);\n\n  // Load the maggot texture\n  const texture = await Assets.load('https://pixijs.com/assets/maggot_tiny.png');\n\n  // Create a container for all the maggots particles and add it to the stage\n  const sprites = new Container();\n\n  app.stage.addChild(sprites);\n\n  // Create an array to store all the sprites\n  const maggots = [];\n\n  const totalSprites = 10000;\n\n  for (let i = 0; i < totalSprites; i++) {\n    // Create a new maggot Sprite\n    const dude = new Sprite(texture);\n\n    // Set the anchor point so the texture is centerd on the sprite\n    dude.anchor.set(0.5);\n\n    // Different maggots, different sizes\n    dude.scale.set(0.8 + Math.random() * 0.3);\n\n    // Scatter them all\n    dude.x = Math.random() * app.screen.width;\n    dude.y = Math.random() * app.screen.height;\n\n    dude.tint = Math.random() * 0x808080;\n\n    // Create a random direction in radians\n    dude.direction = Math.random() * Math.PI * 2;\n\n    // This number will be used to modify the direction of the sprite over time\n    dude.turningSpeed = Math.random() - 0.8;\n\n    // Create a random speed between 0 - 2, and these maggots are slooww\n    dude.speed = (2 + Math.random() * 2) * 0.2;\n\n    dude.offset = Math.random() * 100;\n\n    // Finally we push the dude into the maggots array so it it can be easily accessed later\n    maggots.push(dude);\n\n    sprites.addChild(dude);\n  }\n\n  // Create a bounding box box for the little maggots\n  const dudeBoundsPadding = 100;\n  const dudeBounds = new Rectangle(\n    -dudeBoundsPadding,\n    -dudeBoundsPadding,\n    app.screen.width + dudeBoundsPadding * 2,\n    app.screen.height + dudeBoundsPadding * 2,\n  );\n\n  let tick = 0;\n\n  app.ticker.add(() => {\n    // Iterate through the sprites and update their position\n    for (let i = 0; i < maggots.length; i++) {\n      const dude = maggots[i];\n\n      dude.scale.y = 0.95 + Math.sin(tick + dude.offset) * 0.05;\n      dude.direction += dude.turningSpeed * 0.01;\n      dude.x += Math.sin(dude.direction) * (dude.speed * dude.scale.y);\n      dude.y += Math.cos(dude.direction) * (dude.speed * dude.scale.y);\n      dude.rotation = -dude.direction + Math.PI;\n\n      // Wrap the maggots\n      if (dude.x < dudeBounds.x) {\n        dude.x += dudeBounds.width;\n      } else if (dude.x > dudeBounds.x + dudeBounds.width) {\n        dude.x -= dudeBounds.width;\n      }\n\n      if (dude.y < dudeBounds.y) {\n        dude.y += dudeBounds.height;\n      } else if (dude.y > dudeBounds.y + dudeBounds.height) {\n        dude.y -= dudeBounds.height;\n      }\n    }\n\n    // Increment the ticker\n    tick += 0.1;\n  });\n})();\n"}}),"\n"]}),"\n",(0,a.jsxs)(n.li,{children:["\n",(0,a.jsxs)(n.p,{children:[(0,a.js